import { useEffect, useState } from "react";
import MyButton from "../../UI/MyButton/MyButton";
import classes from "../styles.module.css"
import ChangeEventForm from "./ChangeEventFrom";

const EventItem = (props) =>
{
    const [modal, setModal] = useState(false)

    return (
        <div className={classes.singleEvent}>
            <p>Название: {props.event.name}</p>
            <p>Описание: {props.event.description}</p>
            <p>Дата добавления на сайт: {props.event.date}</p>
            <MyButton onClick = {() => props.remove(props.event, props.identificate)}>Удалить</MyButton>

            <ChangeEventForm event={props.event}></ChangeEventForm>
        </div>
    )
}

export default EventItem;
