import axios from 'axios'

axios.defaults.baseURL = 'http://127.0.0.1:8000/eduschedule'

export default class AuthorizationServices
{
    static async login(data)
    {
        if(localStorage.getItem('ACCESS'))
        {
            localStorage.removeItem('ACCESS')
            localStorage.removeItem('REFRESH')
            delete axios.defaults.headers.common['Authorization'];
        }

        const responce = await axios.post('login', data)
        .catch(error => console.log(error))
        
        localStorage.setItem('ACCESS', responce.access)
        localStorage.setItem('REFRESH', responce.refresh)
        
    }
}
