import { useEffect } from "react"
import MyInput from "../../UI/MyInput/MyInput"
import MyButton from "../../UI/MyButton/MyButton"

const ChangeMark = ({mark, change}) =>
{
    const [data, setData] = useEffect({id: '', value: '', subject: ''})

    const changeMark = () =>
    {
        const newMark = {id:mark.id, value: data, subject:mark.subject}
        change(newMark)
        setData(0)
    }
    return(
        <div>
            <MyInput value = {data.value} placeholder="Новая оценка"
            onChange={e=>setData(...data, value = e.target.value)}/>
            <MyButton onClick={changeMark}>Изменить</MyButton>
        </div>
    )
}

export default ChangeMark;
