import axios from "axios";
import { useState } from "react";
import ListOfDays from "./mini_components/ListOfDays";
import Loader from "../UI/MyLoader/Loader";
import ClassSelect from "./mini_components/ClassSelect";

const Schedule = () =>
{
    const [visible, setVisible] = useState(false)
    const [selectedItem, setSelectedItem] = useState('');
    const [schedule, setSchedule] = useState([])

    async function getShedule()
    {
        const responce = await axios.get(`http://127.0.0.1:8000/eduschedule/api/v1/schedule/${selectedItem}/`)
        setSchedule(responce.data)
    }

    const ChangeOption = (value) =>
    {
        if(value)
        {
            setVisible(true)
            setSelectedItem(value)
            console.log(selectedItem)
            getShedule()

        }
        else
        {
            setVisible(false)
        }
        
    }

    return (
        <div>
            <h1>Расписание</h1>
            <ClassSelect changeSchedule={ChangeOption} selectedItem={selectedItem}/>
            {visible
            ? <ListOfDays schedule={schedule}/>
            : <Loader />
            }
            
        </div>
        
    )
}

export default Schedule;
