import axios from "axios";
import DayOfWeek from "./mini_components/DayOfWeek";
import { useEffect, useState } from "react";
import MyButton from "../UI/MyButton/MyButton";

const Schedule = () =>
{
    const [schedule, setSchedule] = useState([])

    async function getShedule()
    {
        const responce = await axios.get("http://127.0.0.1:8000/eduschedule/api/v1/schedule/1/")
        console.log(responce.data)    
    }

    useEffect(() =>{
        getShedule()
    }, [])
    return (
        <div>
            <h1 style={{textAlign: 'center'}}>Расписание</h1>
            {schedule
            ?
            <DayOfWeek lessons = {schedule.filter(day => day.day_of_week === "monday")} day="monday"/>
            :
            <h1>Расписание не было предоставленно</h1>
            }
            
            <MyButton onClick = {getShedule} />
        </div>
        
    )
}

export default Schedule;
