import axios from 'axios'

axios.defaults.baseURL = 'http://127.0.0.1:8000/eduschedule'

const AuthorizationServices = 
{
    async login(d, setT) 
    {
        await axios.post('login', d)
        .then(response => 
        {
            localStorage.setItem('refresh', response.data)
            axios.defaults.headers.common['Authorization'] = `Bearer ${localStorage.getItem('refresh')}`
        })

        .catch(error => {console.log(error)})
    },
}

export default AuthorizationServices
