import axios from 'axios'
import { useState } from "react";

axios.defaults.baseURL = 'http://127.0.0.1:8000/eduschedule'
const Authorization = () =>
{
    const [data, setData] = useState({login: '', password: '',  is_staff: false})

    const sendRequest = () =>
    {
        alert(JSON.stringify(data))
        axios.post('login', data)
        setData({login: '', password: '', is_staff: false})
    }
    return(
        <div>
            <form>
                <h1>Авторизация</h1>
                <label htmlFor='login'>Логин:</label>
                <input type='text' name='login'
                value={data.login} onChange={event => setData({...data, login: event.target.value})}
                />
                <label htmlFor='password'>Пароль:</label>
                <input type='password' name = 'password'
                value={data.password} onChange={event => setData({...data, password: event.target.value})}
                /><br/><br/>
                <input type='checkbox' value = {data.is_staff}
                onChange={event => setData({...data, is_staff: event.target.checked})}/><br/><br/>
                <button type='submit' onClick={sendRequest}>Войти</button>
            </form>
        </div>
    )
}

export default Authorization;
