import axios from "axios";
import DayOfWeek from "./mini_components/DayOfWeek";
import { useEffect, useState } from "react";
import ListOfDays from "./mini_components/ListOfDays";
import Loader from "../UI/MyLoader/Loader";

const Schedule = () =>
{
    const [visible, setVisible] = useState(false)
    const [selectedItem, setSelectedItem] = useState('');
    const [schedule, setSchedule] = useState([])

    async function getShedule()
    {
        const responce = await axios.get(`http://127.0.0.1:8000/eduschedule/api/v1/schedule/${selectedItem}/`)
        setSchedule(responce.data)
    }

    const ChangeOption = (event) =>
    {
        if(event.target.value)
        {
            setVisible(true)
            setSelectedItem(event.target.value)
            getShedule()
        }
        
    }

    useEffect(() =>{
        getShedule()
    }, [])

    return (
        <div>
            <h1>Расписание</h1>
            <select value={selectedItem} onChange={ChangeOption}>
                <option value=''>Выберите опцию</option>
                <option value='1'>11'A'</option>
                <option value='2'>9'A'</option>
                <option value='3'>5'Б'</option>
            </select>

            {visible
            ? <ListOfDays schedule={schedule}/>
            : <Loader />
            }
            
        </div>
        
    )
}

export default Schedule;
