import { useState } from "react";
import EventList from "./mini_components/EventList"
import ModalWidnow from "./mini_components/ModalWindow";

const Events = () =>
{
    const [events, setEvents] = useState
    ([
        {id: 1, name: 'Линейка', body: 'Долгожданная линейка первоклассников'},
        {id: 2, name: 'Родительское собрание', body: 'Беседа инспектора c родителями школьников'}
    ])


    const createEvent = (newEvent) =>
    {
        setEvents([...events, newEvent])
    }
    const removeEvent = (event) =>
    {
        setEvents(events.filter(e => e.id !== event.id))
    }

    return (
        <div>
            <ModalWidnow></ModalWidnow>
            <EventList events = {events} create = {createEvent} remove = {removeEvent}></EventList>
        </div>
    )
}

export default Events;
