import { useEffect, useState } from "react"
import MyInput from "../../UI/MyInput/MyInput"
import classes from "../styles.module.css"
import MyButton from "../../UI/MyButton/MyButton"


const ChangeEventForm = ({event, change}) =>
{
    const [data, setData] = useState({name: '', description: ''})
    
    useEffect(() => {
        setData(event)
    },[])

    const changeEvent = () =>
    {
        change(data)
        setData({name: '', description: ''})
    }

    return (
        <div className={classes.AddNewForm}>
            <MyInput placeholder = 'Название' value = {data.name} 
            onChange = {e => setData({...data, name: e.target.value})}></MyInput>
            <MyInput placeholder = 'Краткое описание' value = {data.description}
            onChange = {e => setData({...data, description: e.target.value})}></MyInput>   
            <button className={classes.button} onClick ={e => changeEvent()}>Изменить</button>
        </div>
    )
}

export default ChangeEventForm;
