import axios from "axios";
import DayOfWeek from "./mini_components/DayOfWeek";
import { useState } from "react";

const Schedule = () =>
{
    const [schedule, setSchedule] = useState([])
    async function getShedule()
    {
        
        setSchedule(axios.get("http://127.0.0.1:8000/eduschedule/api/v1/schedule/1/"))    
    }

    return (
        <div>
            <h1 style={{textAlign: 'center'}}>Расписание</h1>
            <DayOfWeek lessons = {schedule.filter(day => day.day_of_week === "monday")}/>
        </div>
        
    )
}

export default Schedule;
