import { useState } from "react";
import MyButton from "../../UI/MyButton/MyButton"
import MyInput from "../../UI/MyInput/MyInput";
import classes from "../styles.module.css"

const AddNewForm = ({create}) =>
{
    const [data, setData] = useState({name: '', body: ''})

    const addNewEvet = (event) =>
    {
        event.preventDefault()
        const newEvent = {id: Date.now(), ...data}
        create(newEvent)
        setData({name: '', body: ''})

    }

    return (
        <div>
            <form className={classes.AddNewForm}>
                <MyInput value ={data.name} placeholder="Название" onChange={e=>setData({...data, name: e.target.value})}/>
                <MyInput value = {data.body} placeholder="Описание" onChange={e=>setData({...data, body: e.target.value})}/>
                <MyButton onClick = {addNewEvet}>Добавить</MyButton>
            </form> 
        </div>
    )
}

export default AddNewForm;
