import { useState } from "react";
import MyButton from "../UI/MyButton/MyButton"
import MyInput from "../UI/MyInput/MyInput";

const AddNewForm = ({create}) =>
{
    const [data, setData] = useState({name: '', date: '', time: '', place: '', body: ''})

    const addNewEvet = (event) =>
    {
        event.preventDefault()
        const newEvent = {id: Date.now(), ...data}
        create(newEvent)
        setData({name: '', date: '', time: '', place: '', body: ''})

    }

    return (
        <div>
            <form>
                <MyInput value ={data.name} placeholder="Название" onChange={e=>setData({...data, name: e.target.value})}/>
                <MyInput value = {data.date} placeholder="Дата" onChange={e=>setData({...data, date: e.target.value})}/>
                <MyInput value = {data.time} placeholder="Время" onChange={e=>setData({...data, time: e.target.value})}/>
                <MyInput value = {data.place} placeholder="Место" onChange={e=>setData({...data, place: e.target.value})}/>
                <MyInput value = {data.body} placeholder="Описание" onChange={e=>setData({...data, body: e.target.value})}/>
                <MyButton>Добавить</MyButton>
            </form> 
        </div>
    )
}

export default AddNewForm;
