import axios from "axios";

export default class ScheduleService 
{
    static async getAll(value)
    {
        const responce = await axios.get(`http://127.0.0.1:8000/eduschedule/api/v1/schedule/${value}/`,
        {headers: {Authorization: `Bearer ${localStorage.getItem('ACCESS')}`}})
        .catch(()=>
        {
            if(localStorage.getItem('REFRESH'))
            {
                axios.post('http://127.0.0.1:8000/token/refresh/', localStorage.getItem('REFRESH'))
                .then(responce => {
                    localStorage.setItem('ACCESS', responce.access)
                })
            }
        })
        return responce.data
    }
}
