const ClassSelect = ({changeSchedule, selectedItem}) =>
{
    return (
        <div style={{textAlign : 'left'}}>
            <strong>Выберите класс: </strong>
            <select value={selectedItem} onChange={enevt => changeSchedule(event.target.value)}>
                <option value=''>Выберите класс</option>
                <option value='1'>11'A'</option>
                <option value='2'>9'A'</option>
                <option value='3'>5'Б'</option>
            </select>
        </div>
    )
}

export default ClassSelect;
