import { useState } from "react";
import AddNewForm from "./AddNewForm";
import Event from "./EventItem";
import ModalWidnow from "./ModalWindow";

const EventList = ({remove, create, ...props}) =>
{
    const [modal, setModal] = useState(false)
    
    return (
        <div>
            <h1 style={{textAlign: 'center'}}>Список школьных событий</h1>  
            <ModalWidnow>
                <AddNewForm create = {create}/>
            </ModalWidnow>  

            {props.events.length !== 0
            ? 
                props.events.map(event =><Event remove = {remove} event = {event} key = {event.id}/>)
            : 
                <h2 style={{textAlign: 'center'}}>События отсутствуют</h2>
            }
            
        </div>
    )
}

export default EventList;
