import { useState } from "react";
import EventList from "./mini_components/EventList"
import ModalWidnow from "./mini_components/ModalWindow";
import MyButton from "../UI/MyButton/MyButton";
import AddNewForm from "./mini_components/AddNewForm";

const Events = () =>
{
    const [events, setEvents] = useState
    ([
        {id: 1, name: 'Линейка', body: 'Долгожданная линейка первоклассников'},
        {id: 2, name: 'Родительское собрание', body: 'Беседа инспектора c родителями школьников'}
    ])
    const [modal, setModal] = useState(false)


    const createEvent = (newEvent) =>
    {
        setEvents([...events, newEvent])
        setModal(false)
    }
    const removeEvent = (event) =>
    {
        setEvents(events.filter(e => e.id !== event.id))
    }

    return (
        <div>
            <h1 style={{textAlign: 'center'}}>Список школьных событий</h1> 

            <MyButton style={{marginTop: 15}} onClick= {() => setModal(true)}>Добавить событие</MyButton> 

            <ModalWidnow visible={modal} setVisible={setModal}>
                <AddNewForm create={createEvent}></AddNewForm>
            </ModalWidnow>

            <EventList events = {events} remove = {removeEvent}></EventList>
        </div>
    )
}

export default Events;
