import { useState } from "react"
import classes from "../marks.module.css"
import ChangeMark from "./ChangeMark";
import ModalWidnow from "../../Events/mini_components/ModalWindow";

const MarkField = ({children, change}) =>
{   
    const [visible, setVisible] = useState(false)
    return (
        <div className={classes.markField}>
            <ModalWidnow visible={visible}>
                <ChangeMark change={change}/> 
            </ModalWidnow>

            <p onClick={e => setVisible(!visible)}>{children.value}</p>
        </div>
    )
}

export default MarkField;
