import MyButton from "../UI/MyButton/MyButton";
import MyInput from "../UI/MyInput/MyInput";
import classes from "./styles.module.css"

const Event = (props) =>
{

    return (
        <div className={classes.singleEvent}>
            <p><label>Название: <MyInput value = {props.event.name}/></label></p>
            <p>Дата: {props.event.date}</p>
            <p>Время: {props.event.time}</p>
            <p>Описание: {props.event.body}</p>
            <p>Место: {props.event.place}</p>
            <MyButton onClick = {() => props.remove(props.event)}>Удалить</MyButton>
            <MyButton>Изменить</MyButton>
        </div>
    )
}

export default Event;
