import axios from "axios";
import DayOfWeek from "./mini_components/DayOfWeek";
import { useEffect, useState } from "react";

const Schedule = () =>
{
    const [visible, setVisible] = useState(false)
    const [selectedItem, setSelectedItem] = useState('');
    const [schedule, setSchedule] = useState([])

    async function getShedule()
    {
        const responce = await axios.get("http://127.0.0.1:8000/eduschedule/api/v1/schedule/1/")
        setSchedule(responce.data)
    }

    const ChangeOption = (event) =>
    {
        if(event.target.value)
            console.log(1)
        setSelectedItem(event.target.value)
    }

    useEffect(() =>{
        getShedule()
    }, [])

    return (
        <div>
            <h1>Расписание</h1>
            <select value={selectedItem} onChange={ChangeOption}>
                <option value=''>Выберите опцию</option>
                <option value='1'>11'A'</option>
                <option value='2'>9'A'</option>
                <option value='3'>5'Б'</option>
            </select>
            <DayOfWeek lessons={schedule.filter(days => days.day_of_week === "Понедельник")} day="Понедельник"/>
            <DayOfWeek lessons={schedule.filter(days => days.day_of_week === "Вторник")} day="Вторник"/>
            <DayOfWeek lessons={schedule.filter(days => days.day_of_week === "Среда")} day="Среда"/>
            <DayOfWeek lessons={schedule.filter(days => days.day_of_week === "Четверг")} day="Четверг"/>
            <DayOfWeek lessons={schedule.filter(days => days.day_of_week === "Пятница")} day="Пятница"/>
        </div>
        
    )
}

export default Schedule;
