import { useState } from "react";
import AuthorizationServices from '../services/AutorizationServices'
import MyInput from "../UI/MyInput/MyInput";
import MyButton from "../UI/MyButton/MyButton";

const Authorization = () =>
{
    const [data, setData] = useState({login: '', password: '',  is_staff: false})
    async function sendRequest(event)
    {
        event.preventDefault()
        if(data.login && data.password)
        {
            await AuthorizationServices.login(data)
            window.location.href = "http://localhost:3000/home"
        }
        else
        {
            alert("Не все поля заполнены.\nПовторите попытку")
        }
        
        setData({login: '', password: '', is_staff: false})
    }

    return(
        <div>
            <form>
                <h1 style={{textAlign: 'center'}}>Авторизация</h1>
                <div style={{textAlign : 'center'}}>
                    <MyInput type='text' name='login' placeholder="Логин"
                    value={data.login} onChange={event => setData({...data, login: event.target.value})}
                    style = {{width : "40%"}} /><br/><br/>
                    <MyInput type='password' name = 'password' placeholder="Парольй"
                    value={data.password} onChange={event => setData({...data, password: event.target.value})}
                    style = {{width : "40%"}}/><br/><br/>
                    <input type='checkbox' value = {data.is_staff}
                    onChange={event => setData({...data, is_staff: event.target.checked})}/><br/><br/>
                    <MyButton onClick={sendRequest}>Войти</MyButton>
                </div>
            </form>
        </div>
    )
}

export default Authorization;
