        {id: 2, name: 'Родительское собрание', date: '05.09.2023', time: '18:00', body: 'Беседа инспектора c родителями школьников', place: 'Актовый зал'}
    ])


    const createEvent = (newEvent) =>
    {
        setEvents([...events, newEvent])
    }
    const removeEvent = (event) =>
    {
        setEvents(events.filter(p => p.id !== postMessage.id))
    }

